mapper.insert:

	insert into event_tb(
	
		eventCnt 
		, eventTitle 
		, userIndex 
		)values(
		#{eventCnt} 
		, #{eventTitle} 
		, #{userIndex} 
		)
		
	
		select max(eventIndex) from event_tb

mapper.update:
update event_tb
	set eventCnt = #{eventCnt}
	, eventTitle = #{eventTitle}
	where eventIndex = #{eventIndex}

update event_tb
	set eventHit = eventHit + 1
	where eventIndex = #{eventIndex}
