mapper.insert:

	insert into free_tb(
	
		freeCnt 
		, freeTitle 
		, userIndex 
		)values(
		#{freeCnt} 
		, #{freeTitle} 
		, #{userIndex} 
		)
		
	
		select max(freeIndex) from free_tb

mapper.update:
update free_tb
	set freeCnt = #{freeCnt}
	, freeTitle = #{freeTitle}
	where freeIndex = #{freeIndex}

update free_tb
	set freeHit = freeHit + 1
	where freeIndex = #{freeIndex}
