mapper.insert:

	insert into re_tb(
		reCnt 
		, freeIndex
		, userIndex 
		)values(
		#{reCnt} 
		, #{freeIndex}
		, #{userIndex} 
		)

mapper.update:
update re_tb
	set reCnt = #{reCnt}
	where reIndex = #{reIndex}

